import React from 'react';
import './ImageLinkForm.css';
import { Button, Modal } from 'react-bootstrap'


const ImageLinkForm = ({onInputChange, onButtonSubmit} ) => {
    handleModal();{
        this.setState({show:!this.state.show});
      }
    
    return (
        <div>
            <p className='f2 washed-blue'>  
                {'This Brain will detect faces in your picture'}
            </p>
            <div className = 'center'>
                <div className = 'form center pa4 br3 shadow-5'>
                    <input className = 'f4 pa2 w-70 center' type='tex' onChange={onInputChange}/>
                    <button
                        className = 'grow link ph3 pv2 dib white buttonS' 
                        onClick={onButtonSubmit}
                    >detect</button>
                </div>
                <div > 
                <Button className="btn-openModal" onClick={() =>{this.handleModal()}}>Open Modal</Button>
                <Modal className="modal-wrapper" show={this.state.show} onHide={() =>this.handleModal()} >
                  <Modal.Header>Modal Head Part</Modal.Header>
                  <Modal.Body className="modal-content">
                    Hi, React modal is here
                  </Modal.Body>
                  <Modal.Footer>
                    <Button onClick={() =>{this.handleModal()}}>
                      Close Modal
                    </Button>
                  </Modal.Footer>
                </Modal>
                </div>
            </div>
        </div>
    )
}

export default ImageLinkForm;
